// check if documet is ready, then begin your thing
document.onreadystatechange = () => {
  if (document.readyState === "complete") {
    initialize_game();
  }
};

// First function
var initialize_game = function() {
  console.log("Game started..");

  let h1Element = document.createElement("h1");
  h1Element.innerHTML = generate_random_string(1);
  h1Element.setAttribute("id", "h1-val");
  h1Element.style.fontSize = "400px";

  let centerElement = document.createElement("center");
  centerElement.appendChild(h1Element);

  document.getElementById("kbgame-app").appendChild(centerElement);
};

// Second Function
function generate_random_string(string_length) {
  let random_string = "";
  let random_ascii;
  let ascii_low = 97;
  let ascii_high = 122;
  if (Math.floor(Math.random() * 2) === 0) {
    ascii_low = 65;
    ascii_high = 90;
  }
  for (let i = 0; i < string_length; i++) {
    random_ascii = Math.floor(
      Math.random() * (ascii_high - ascii_low) + ascii_low
    );
    random_string += String.fromCharCode(random_ascii);
  }
  return random_string;
}

// Check the Answer is right or not
document.onkeydown = checkKey;
function checkKey(e) {
  e = e || window.event;

  if (document.getElementById("h1-val").innerHTML.length > 0) {
    let charCode = document.getElementById("h1-val").innerHTML.charCodeAt(0);
    if (e.keyCode === charCode - 32 || e.keyCode === charCode) {
      console.log("matched");
      for (let i = 0; i < 50; i++) {
        beep();
      }
      document.getElementById("h1-val").style.color = "green";
      setTimeout(() => {
        document.getElementById("h1-val").innerHTML = generate_random_string(1);
        document.getElementById("h1-val").style.color = "black";
      }, 1000);
    } else {
      console.log("not matched");
      beep();
      document.getElementById("h1-val").style.color = "red";
      setTimeout(() => {
        document.getElementById("h1-val").style.color = "black";
      }, 1000);
    }
  }
}
// For Sound We need to write another function
function beep() {
  var snd = new Audio(
    "data:audio/wav;base64,"
  );
  snd.play();
}
